import React from 'react';
import MessagePane from './components/messagePane.js';
import ContactPane from './components/contactPane.js';
import Navbar from './components/navbar.js'

class BasePage extends React.Component {

   constructor () {
      super();
      this.toggleMessagePane = this.toggleOnlyMessagePane.bind(this);

      this.state = {
         onlyMessagePane: false
      }
   }

   toggleOnlyMessagePane() {

      alert("a");
      this.setState({showMessagePane: !this.state.onlyMessagePane});
   }

   render(){
      return(
         <div class="page">
            <div class="navbar">
               <Navbar/>
            </div>

               {
                  this.state.toggleMessagePane ? (
                     <div class="container">
                        <ContactPane showClass={"mobileContactHide"} toggleMobileView={this.toggleOnlyMessagePane} />
                        <MessagePane showClass={"mobileMessageShow"} toggleMobileView={this.toggleOnlyMessagePane} />
                     </div>
                  ) : (
                     <div class="container">
                        <ContactPane showClass={"mobileContactShow"} toggleMobileView={this.toggleOnlyMessagePane} />
                        <MessagePane showClass={"mobileMessageHide"} toggleMobileView={this.toggleOnlyMessagePane} />
                     </div>
                  )
               }

         </div>
      );
   }
}
export default BasePage;
