import React from 'react';
import MessagePane from './components/messagePane.js';
import ContactPane from './components/contactPane.js';
import Navbar from './components/navbar.js'

class BasePage extends React.Component {

   constructor (props) {
      super(props);
      this.toggleMessagePane = this.toggleOnlyMessagePane.bind(this);

      this.state = {
         onlyMessagePane: false
      }

   }

   toggleOnlyMessagePane = () => {
      console.log("a");
      alert("a");
      this.setState({showMessagePane: !this.state.onlyMessagePane});
   }

   render(){
      return(
         <div class="page">
            <div class="navbar">
               <Navbar/>
            </div>

               {
                  this.state.toggleMessagePane ? (
                     <div class="container">
                        <ContactPane toggleMobileView={this.toggleOnlyMessagePane} showClass={"mobileContactHide"}  />
                        <MessagePane toggleMobileView={this.toggleOnlyMessagePane} showClass={"mobileMessageShow"}  />
                     </div>
                  ) : (
                     <div class="container">
                        <ContactPane toggleMobileView={this.toggleOnlyMessagePane} showClass={"mobileContactShow"}  />
                        <MessagePane toggleMobileView={this.toggleOnlyMessagePane} showClass={"mobileMessageHide"}  />
                     </div>
                  )
               }

         </div>
      );
   }
}
export default BasePage;
